&#x2F;&#x2F; router.js
&#x2F;&#x2F; 需响应的文件路由

const http &#x3D; require(&quot;http&quot;);

&#x2F;&#x2F; 导入文件系统模块
&#x2F;&#x2F; 所有的文件系统操作都具有同步的、回调的、以及基于 promise 的形式。
const fs &#x3D; require(&quot;fs&quot;);

const index &#x3D; require(&quot;.&#x2F;file&#x2F;index&quot;); &#x2F;&#x2F; 主页路由模块
const error &#x3D; &quot;.&#x2F;file&#x2F;error404.html&quot;; &#x2F;&#x2F; 错误处理路由
const poem &#x3D; &quot;.&#x2F;file&#x2F;poem.html&quot;;       &#x2F;&#x2F; 古诗路由

let Response &#x3D; function(res, filePath) &#123;
    fs.readFile(filePath, (err, data) &#x3D;&gt; &#123;
        if(err) &#123;
            res.writeHead(404, &#123;
                &quot;Content-Type&quot;: &quot;text&#x2F;html&quot;
            &#125;);
            res.end(&quot;error&quot;)
        &#125; else &#123;
            res.writeHead(200, &#123;
                &quot;Content-Type&quot;: &quot;text&#x2F;html&quot;
            &#125;);
            res.end(data);
        &#125;
    &#125;)
&#125;


let router &#x3D; function(req, res) &#123;
    if(req.url &#x3D;&#x3D; &#39;&#x2F;index&#39; || req.url &#x3D;&#x3D; &#39;&#x2F;&#39;) &#123; &#x2F;&#x2F;主页
        index.index(res);
    &#125; else if(req.url &#x3D;&#x3D; &#39;&#x2F;poem&#39;) &#123;
        Response(res, poem);
    &#125; else &#123;
        Response(res, error);
    &#125;
&#125;

exports.router &#x3D; router;


&#x2F;&#x2F; server.js
&#x2F;&#x2F; 服务端的start函数, 用于开启一个web服务器

&#x2F;&#x2F; 导入http模块，使用HTTP服务器和客户端

&#x2F;&#x2F; Node中的HTTP API是非常底层的，
&#x2F;&#x2F; 仅进行流处理和消息解析。它将消息解析为消息头和消息体，但不会解析具体的消息头或消息体
const http &#x3D; require(&quot;http&quot;);

&#x2F;&#x2F; 导入url模块，用于处理与解析 URL。

&#x2F;&#x2F; URL字符串是结构化的字符串，包含多个含义不同的组成部分。解析后返回的是URL对象
&#x2F;&#x2F; url 模块提供了两套 API 来处理 URL：一个是旧版本传统的 API，一个是实现了 WHATWG标准的新 API。
const url &#x3D; require(&quot;url&quot;);

const router &#x3D; require(&#39;.&#x2F;router&#39;)


function start() &#123;
    function onRequest(req, res) &#123;
        if(req.url &#x3D;&#x3D;&#x3D; &#39;&#x2F;favicon.ico&#39;) &#123;
            &#x2F;&#x2F; 浏览器默认会请求网站根目录的favicon.ico图标，如果网站根目录没有会产生 404。出于优化的考虑，要么就有这个图标，要么就禁止产生这个请求。
            return ;
        &#125;

        console.log(&#39;End of line&#39;);
        console.log(req.url);

        let myUrl &#x3D; url.parse(req.url);
        console.log(myUrl);

        router.router(req, res)
    &#125;

    http.createServer(onRequest).listen(&#39;8888&#39;)
    console.log(&quot;Server is running at &quot; + &quot;http:&#x2F;&#x2F;localhost:8888&quot;);
&#125;

exports.start &#x3D; start;
